import config from '../../config.json';
import httpServices from './../httpServices';

const login = async (usr) => {
    const {data:aut} = await httpServices.post(config.path,usr);
    console.log(aut);
    if (aut.val) {
        localStorage.setItem('token',aut.token);
        return true;
    }
    return false;
    
}

const signout = () => {
    localStorage.removeItem('token');
    return false;
}

const getSession = () => {
    const token = localStorage.getItem('token');
    if (token){
        return token
    } 
    return false
}
 

export default {
    login,
    signout,
    getSession
}
