import React, { Component } from 'react';


class Counter extends Component {
    // constructor(){
    //     super();
    //     this.handleIncrement = this.handleIncrement.bind(this)
    //     console.log('constructor', this)
    // }
    render() {
        let obj = this.props.prop
        return ( 
        <React.Fragment>
            <div className="col-md-3">
                <span className={this.getBadgeClass(obj.value)+" col-md-2"}>{this.formatcount(obj.value)}</span>
                <button 
                    onClick={() => obj.onIncrement(obj.id)} 
                    className="btn btn-secondary btn-sm m-2"
                >
                    +
                </button>
                <button 
                    onClick={() => obj.onDecrement(obj.id)} 
                    className="btn btn-secondary btn-sm m-2"
                    disabled={obj.value === 0 ? true : false}
                >
                    -
                </button>
                <button
                    onClick={() => obj.onDelete(obj.id )}
                    className="btn btn-danger btn-sm m-2"
                >
                    Delete
                </button>
            </div>
        </React.Fragment> 
        );
    }

    getBadgeClass(val) {
        let classes = "badge m-2 badge-";
        classes += val === 0 ? "warning" : "primary";
        return classes;
    }

    formatcount(val){
        return val === 0 ? 'Zero' : val;
    }
}
 
export default Counter;
