import React, { Component } from 'react';
import Movies from './movies';
// import Counter from './counterComponet';
import Pagination from './pagination';


class Counters extends Component {

    filterCrimes = (crimes, cat) => {
        const categories = cat !== '' ? crimes.filter(c => {
           if (c.row.category_id === cat){
               return c.row.category_id
           }
        }) : crimes.row.category_id;
        return categories;
    }
    render() { 
        const elements = selectedCategory !== '' ? this.filterCrimes(arr, selectedCategory) : arr;

        return (   
            <React.Fragment>
                {/* <Counter prop = {this.props} /> */}
                <Movies prop = {this.props} elements={elements} />
                <Pagination 
                    itemsCount={this.elements.length} 
                    pageSize={this.props.pageSize} 
                    currentPage={this.props.currentPage} 
                    onPageChange={this.props.onPageChange} 
                />
            </React.Fragment>
         );
    }

    
}
 
export default Counters;
