import React, { Component } from 'react';
import _ from 'lodash';
import PropTypes from 'prop-types';

class Pagination extends Component {
    render() { 
        const { itemsCount, pageSize, onPageChange, currentPage } = this.props;
        const pagesCount = Math.ceil(itemsCount / pageSize);
        if (pagesCount === 1){ return null }
        const pages = _.range(1,pagesCount + 1);

        return ( 
            <React.Fragment>
                <div className="container">
                    <nav aria-label="Page navigation example">
                        <ul className="pagination">
                            <li className="page-item"><a className="page-link" href="#">Previous</a></li>
                            {pages.map(page => 
                                <li className={currentPage === page ? "page-item active" : "page-item"} key={page}><a className="page-link"  onClick={() => onPageChange(page)} >{page}</a></li>
                            )}
                            <li className="page-item"><a className="page-link" href="#">Next</a></li>
                        </ul>
                    </nav>
                </div>
            </React.Fragment>
         );
    }
}

// Pagination.propTypes = {
//     itemsCount: PropTypes.number.isRequired,
//     pageSize: PropTypes.number.isRequired,
//     onPageChange: PropTypes.number.isRequired,
//     currentPage: PropTypes.number.isRequired
// }
export default Pagination;
