import React, { Component } from 'react';

class ListGroup extends Component {
    render() { 
        const { allCategories, selectedCategory, onCategoryChange } = this.props;
        return ( 
            <ul className="list-group">
                 <li key="allCategories" className={ selectedCategory === "allCategories" ? "list-group-item active" : "list-group-item"} onClick={() => onCategoryChange("allCategories")}>{ "All Categories" }</li>
                {allCategories.map(out => (
                    
                     <li typeof='button' key={ out } className={ selectedCategory === out ? "list-group-item active" : "list-group-item"} onClick={() => onCategoryChange(out)}>{ out }</li>
                    ) 
                )}
                
            </ul>
         );
    }
}
 
export default ListGroup;
