import React, { Component } from 'react';
// import Like from './like';
import { paginate } from './paginate';
import ListGroup from './listGroup';
import Pagination from './pagination';
import Crimes from './crimes';
import _ from 'lodash';

class Movies extends Component {
    filterCrimes = (crimes, cat) => {
        const categories = cat !== 'allCategories' ? crimes.filter(c => {
           if (c.category_id === cat){
               return c.category_id
           }
        }) : crimes;
        return categories;
    }
    render() { 
        const { onLike, onDelete, pageSize, currentPage, allCategories, selectedCategory, onCategoryChange, sortColumn,onSort} = this.props.prop;
        const elements = this.props.selectedCategory !== '' ? this.filterCrimes(this.props.out, this.props.selectedCategory) : this.props.out;
        const sorted = _.orderBy(this.props.elements,[sortColumn.path], [sortColumn.order])
        const rows = paginate(sorted, currentPage, pageSize);
        return ( 
            <div className="row">
                <div className="col-3 m-2">
                    <ListGroup 
                        allCategories={ allCategories } 
                        selectedCategory={ selectedCategory } 
                        onCategoryChange={ onCategoryChange} 
                    />
                </div>
                <div className="col m-4">
                    <Crimes 
                        crimes={rows} 
                        onDelete={ onDelete } 
                        onSort={onSort}
                    />
                    <Pagination 
                        itemsCount={this.props.itemsCount} 
                        pageSize={this.props.pageSize} 
                        currentPage={this.props.currentPage} 
                        onPageChange={this.props.onPageChange} 
                    />
                </div>
            </div>
         );
    }
}
 
export default Movies;
