import React, { Component } from 'react';
import { Link, Redirect } from 'react-router-dom';
import CrimeServices from './crimeServices';
import _ from 'lodash';
import ListGroup from './listGroup';
import Pagination from './pagination';
import { paginate } from './paginate';

class Crimes extends Component {
    state = {
        crimes: [],
        crime:[],
        categories: [],
        elements: [],
        selectedCategory:'allCategories',
        currentPage: 1,
        pageSize: 5,
        sortColumn: {path: '', order:''}
    }
    async componentDidMount() {
        const crimes = await CrimeServices.getCrimes();
        console.log(crimes)
        this.setState({crimes});

         const cat = [];
        
        this.state.crimes.map(c => {
              cat.push(c.category_id)
        });
        const categories = _.uniq(cat);
        this.setState({ categories });

        const elements = this.state.selectedCategory === 'allCategories' ?  this.state.crimes: null;
        console.log(elements)
        this.setState({ elements })
    }
    filterCrimes = (crimes, cat) => {
        const categories = cat !== 'allCategories' ? crimes.filter(c => {
           if (c.category_id === cat){
               return c.category_id
           }
        }) : crimes;
        return categories;
    }
    handleSort = (path, sortColumn) => {
        if (sortColumn.path === path){
            sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';
        } else{
            sortColumn.path = path;
            sortColumn.order = 'asc';
        }
        this.setState({sortColumn});
    }
    handlePageChange = page => {
        this.setState({ currentPage:page })
    }
    handleCrimesDetails = e =>{
        console.log(e)
        
        // const crime = CrimeServices.crimeDetails(id);
        // this.setState({crime});
        // return <Redirect to='/edit' />
        return ''

    }
    handlePageChange = page => {
        this.setState({ currentPage:page })
    }
    selectedCategoryChange = category  => {
        const selectedCategory = category;
        this.setState({ selectedCategory });

        const currentPage = 1;
        this.setState({ currentPage });

    }
    render() { 
        const elements = this.state.selectedCategory !== 'allCategories' ? this.filterCrimes(this.state.crimes, this.state.selectedCategory) : this.state.crimes;
        const sorted = _.orderBy(elements,[this.state.sortColumn.path], [this.state.sortColumn.order])
        const rows = paginate(sorted, this.state.currentPage, this.state.pageSize);
        return ( 
            <React.Fragment>
                <div className='row'>
                    <div className="col-3 m-2">
                        <ListGroup 
                            allCategories={ this.state.categories } 
                            selectedCategory={ this.state.selectedCategory } 
                            onCategoryChange={ this.selectedCategoryChange} 
                        />
                    </div>
                    <div className="col m-4">
                        <p>Showing {rows.length} movies in the database.</p>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th scope="col" onClick={() => this.handleSort('id', this.state.sortColumn)}>#</th>
                                    <th scope="col" onClick={() => this.handleSort('category_id', this.state.sortColumn)}>Category</th>
                                    <th scope="col" onClick={() => this.handleSort('latitude', this.state.sortColumn)}>Latitud</th>
                                    <th scope="col" onClick={() => this.handleSort('longitude', this.state.sortColumn)}>Longitude</th>
                                    <th scope="col" onClick={() => this.handleSort('street_name', this.state.sortColumn)}>Street Name</th>
                                    <th scope="col">Outcome</th>
                                    <th scope="col" onClick={() => this.handleSort('month', this.state.sortColumn)}>Month</th>
                                    <th scope="col">Delete</th>
                                </tr>
                            </thead>
                            <tbody>
                                {rows.map(out => (
                                    <tr key={out.id}>
                                        <td>{out.id}</td>
                                        <td>{out.category_id}</td>
                                        <td>{out.latitude}</td>
                                        <td>{out.longitude}</td>
                                        <td>{out.street_name}</td>
                                        <td>{out.outcome_status}</td>
                                        <td>{out.month}</td>
                                        <td>
                                            {/* <form onSubmit={() => this.handleCrimesDetails() }> */}
                                                <button onClick={() => this.handleCrimesDetails(out.id)} type='submit' className='btn btn-primary'>
                                                    Details
                                                </button>
                                            {/* </form>                                         */}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                    <Pagination 
                        itemsCount={elements.length} 
                        pageSize={this.state.pageSize} 
                        currentPage={this.state.currentPage} 
                        onPageChange={this.handlePageChange} 
                    />
                </div>
            </React.Fragment>
         );
    }
}
 
export default Crimes;
