import React, { Component } from 'react';
// import Like from './like';
import { paginate } from './paginate';
import ListGroup from './listGroup';

class Movies extends Component {
    filterCrimes = (crimes, cat) => {
        const categories = cat !== '' ? crimes.filter(c => {
           if (c.row.category_id === cat){
               return c.row.category_id
           }
        }) : crimes.row.category_id;
        return categories;
    }
    render() { 
        const {out:arr, onLike, onDelete, pageSize, currentPage, allCategories, selectedCategory, onCategoryChange} = this.props.prop;
        
        const elements = selectedCategory !== '' ? this.filterCrimes(arr, selectedCategory) : arr;

        const rows = paginate(elements, currentPage, pageSize);
        return ( 
            <div className="row container">
                <div className="col-3 m-2">
                    <ListGroup allCategories={ allCategories } selectedCategory={ selectedCategory } onCategoryChange={ onCategoryChange} />
                </div>
                <div className="col">
                    <p>Showing {arr.length} movies in the database.</p>
                    <table className="table">
                        <thead>
                            <tr>
                                <th scope="col">#</th>
                                <th scope="col">Category</th>
                                <th scope="col">Latitud</th>
                                <th scope="col">Longitude</th>
                                <th scope="col">Street Name</th>
                                <th scope="col">Outcome</th>
                                <th scope="col">Month</th>
                                <th scope="col">Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {rows.map(out => (
                                <tr key={out.row.id}>
                                    <td>{out.row.id}</td>
                                    <td>{out.row.category_id}</td>
                                    <td>{out.row.latitude}</td>
                                    <td>{out.row.longitude}</td>
                                    <td>{out.row.street_name}</td>
                                    <td>{out.row.outcome_status}</td>
                                    <td>{out.row.month}</td>
                                    {/* <td><Like onLike={onLike} liked={movie.movie.liked} movieId={movie.movie.id} /></td> */}
                                    <td>
                                        <button
                                            onClick= {() => onDelete(out.row.id)}
                                            className="btn btn-danger btn-sm"
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
         );
    }
}
 
export default Movies;
