import React, { Component } from 'react';
import Like from './like';

class Movies extends Component {
    state = { movies: [
                {  movie: {
                    id:1,
                    title: "Terminator",
                    genre: {
                        name: "Action"
                    },
                    numberInStock: 6,
                    dailyRentalRate: 2.5,
                    liked: false
                    }
                },
                { movie: {
                    id:2,
                    title: "Die Hard",
                    genre: {
                        name: "Action"
                    },
                    numberInStock: 5,
                    dailyRentalRate: 2.5,
                    liked: true
                    }
                },
                { movie: {
                    id:3,
                    title: "Get Out",
                    genre: {
                        name: "Thriller"
                    },
                    numberInStock: 8,
                    dailyRentalRate: 3.5,
                    liked: true
                    }
                }
            ]
    }

    handleDelete = movieId => {
        id = movieId.movie.id;
        const counters = this.state.movies.map(c => {
            if(c.id === id){
                c.liked = !c.liked;
            }
            return c
        })
        this.setState({ counters })
    }
    handleLike = movieId => {
        id = movieId.movie.id;
        const movies = this.state.movies.filter(c =>{
            if(c.id === id){
                c.liked = !c.liked;
            }
            return c
        })
        this.setState({ counters })
    }
    render() { 
        return ( 
            <div className="container">
                <p>Showing {this.state.movies.length} movies in the database.</p>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Genre</th>
                            <th>Stock</th>
                            <th>Rate</th>
                            <th>Like</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.movies.map(movie => (
                            <tr key={movie.movie.id}>
                                <td>{movie.movie.title}</td>
                                <td>{movie.movie.genre.name}</td>
                                <td>{movie.movie.numberInStock}</td>
                                <td>{movie.movie.dailyRentalRate}</td>
                                <td><Like liked={movie.movie.liked} onClick={this.handleLike(movie)}/></td>
                                <td>
                                    <button
                                        onClick={() => this.handleDelete(movie)}
                                        className="btn btn-danger btn-sm"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
         );
    }
}
 
export default Movies;
