import React, { Component } from 'react';
import NavBar from './navbar';
import Counter from './counters';
import axios from 'axios';
import _ from 'lodash';

class App extends Component {
    state = {
        post:[],
        categories: [],
        selectedCategory: 'violent-crime',
        itemsCount:0,
        pageSize:10,
        currentPage: 1
    }

    async componentDidMount() {
        const {data:post} = await axios.get('http://localhost');
        this.setState({post});
        const itemsCount = this.state.post.length;
        this.setState({itemsCount})
        const {data:categories} = await axios.get('http://localhost/cat');
        this.setState({ categories })

        // const cat = _.uniqBy(this.state.post, 'category_id')
        this.state.post.map(c => {
              console.log(c)
        });
        // const cats = this.getAllCategories(this.state.post, this.state.selectedCategory);
    }
    getAllCategories = (crimes, cat) => {
        const categories = cat !== '' ? crimes.filter(c => {
           if (c.row.category_id === cat){
               return c.row.category_id
           }
        }) : crimes.row.category_id;
        return categories;
    }
    handleReset = () => {
         const counters = this.state.counters.map(
             c => {
                 c.value = 0;
                 return c
             }
         )
         this.setState({ counters })
         
     }
    handleIncrement = counterId =>{
        const counters = this.state.counters.map(
                c => {
                    if ( c.id === counterId){
                        c.value = c.value + 1;
                    }                    
                    return c
                }
            );

        this.setState({ counters })

    }
    handleDecrement = counterId => {
        const counters = this.state.counters.map(
            c => {
                if(c.id === counterId){
                    c.value = c.value - 1;
                }
                return c
            }
        );
        this.setState(counters);
    }
    handleDelete = counterId => {
        const counters = this.state.counters.filter(c => c.id !== counterId)
        this.setState({ counters })
    }
    handleDeleteMovie = movieId => {
        const movies = this.state.movies.filter(c => c.movie.id !== movieId);
        this.setState({ movies });
    }
    handleLike = movieId => {
        let id = movieId;
        const movies = this.state.movies.filter(c =>{
            if(c.movie.id === id){
                c.movie.liked = !c.movie.liked;
            }
            return c
        })
        this.setState({ movies })
    }
    handlePageChange = page => {
        this.setState({ currentPage:page })
    }
    render() { 
        return ( 
            <React.Fragment>
                <NavBar />
                {/* <button onClick={this.handleReset}
                className="btn btn-secondary btn-sm m-2">Reset</button><br></br>
                <div className="row">
                    {this.state.counters.map(counter => (
                        <Counter 
                            key={counter.id} 
                            onDelete={this.handleDelete}
                            value={counter.value} 
                            id={counter.id}
                            onIncrement={this.handleIncrement}
                            onDecrement={this.handleDecrement}
                        />
                    ))}
                </div>                */}
                <Counter 
                    out={this.state.post} 
                    onLike={this.handleLike} 
                    onDelete={this.handleDeleteMovie} 
                    onPageChange={this.handlePageChange} 
                    itemsCount={this.state.itemsCount}
                    pageSize={this.state.pageSize}
                    currentPage={this.state.currentPage}
                />
            </React.Fragment>
         );
    }
}
 
export default App;
