import React, { Component } from 'react';
import CrimeServices from './crimeServices';
import { nominalTypeHack } from 'prop-types';
import crimeServices from './crimeServices';

class CrimeDetails extends Component {
    state = {
        crime: {
            crime: {
                'id':'',
                'category_id':'',
                'location_type':'',
                'latitude':'',
                'longitude':'',
                'street_name':'',
                'persistent_id':'',
                'month':''
            },
            outcomes: []
        }
    }

    async componentWillMount(){
        const crime = await CrimeServices.crimeDetails(this.props.match.params.id);
        this.setState({ crime })
    }
    handleEdit = () => {

        document.getElementById('editOutcomes').disabled = true;
        document.getElementById('cancelEdition').disabled = false;
        document.getElementById('saveEdition').disabled = false;
        var buttons = document.getElementsByClassName('cancel');
        for (var i = 0; i < buttons.length; i++) {
            buttons[i].disabled = false;
        }
          
    }
    handleSave = async () => {

        const obj = {
            'id':document.getElementById('id'),
            'category':document.getElementById('category'),
            'location_type':document.getElementById('location_type'),
            'latitude':document.getElementById('latitude'),
            'longitude':document.getElementById('longitude'),
            'street_name':document.getElementById('street_name'),
            'month':document.getElementById('month')
        }
        crime = await CrimeServices.updateCrime(obj);
        this.setState({ crime });

        // document.getElementById('editOutcomes').disabled = true;
        // document.getElementById('cancelEdition').disabled = false;
        // document.getElementById('saveEdition').disabled = false;
        // var buttons = document.getElementsByClassName('cancel');
        // for (var i = 0; i < buttons.length; i++) {
        //     buttons[i].disabled = false;
        // }
          
    }
    handleCancel = () => {

        document.getElementById('editOutcomes').disabled = false;
        document.getElementById('cancelEdition').disabled = true;
        document.getElementById('saveEdition').disabled = true;
        var buttons = document.getElementsByClassName('cancel');
        for (var i = 0; i < buttons.length; i++) {
            buttons[i].disabled = true;
        }
          
    }
    render() { 
        const { crime } = this.state;
        return ( 
            <React.Fragment>
            <div>
                <div className="container-fluid">
                    <div className="row custom-centered">
                        <div className="col align-self-center">
                            <center>
                                <fieldset>	                                    
                                    <form className="col-md-8" id="editCrime">							
                                        <h3 className="text-uppercase pull-center">CRIME DETAILS</h3>	
                                            <div className="row">
                                                <div className="form-group col-md-6">
                                                    <label htmlFor="id">ID</label>
                                                    <input type="text" name="id" id="id" className="form-control input-lg text-center" value={crime.crime.id} disabled />
                                                </div>
                                                <div className="form-group col-md-6">
                                                    <label htmlFor="category">Category</label>
                                                    <input type="text" name="category" id="category" className="form-control input-lg text-center edit" value={crime.crime.category_id} disabled />
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="form-group col-md-12">
                                                    <label htmlFor="persistent_id">Persistent ID</label>
                                                    <input type="text" name="persistent_id" id="persistent_id" className="form-control input-lg text-center" value={crime.crime.persistent_id} disabled /> 
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="form-group col-md-4">
                                                    <label htmlFor="location_type">Location Type</label>
                                                    <input type="text" name="location_type" id="location_type" className="form-control input-lg text-center edit" value={crime.crime.location_type} disabled />
                                                </div>
                                                <div className="form-group col-md-8">
                                                    <label htmlFor="street_name">Street</label>
                                                    <input type="text" name="street_name" id="street_name" className="form-control input-lg text-center edit" value={crime.crime.street_name} disabled />
                                                </div>
                                            </div>
                                            <div className="row">
                                                <div className="form-group col-md-4">
                                                    <label htmlFor="longitud">Longitud</label>
                                                    <input type="text" name="longitude" id="longitude" className="form-control input-lg text-center edit" value={crime.crime.longitude} disabled />
                                                </div>
                                                <div className="form-group col-md-4">
                                                    <label htmlFor="latitude">Latitud</label>
                                                    <input type="text" name="latitude" id="latitude" className="form-control input-lg text-center edit" value={crime.crime.latitude} disabled />
                                                </div>
                                                <div className="form-group col-md-4">
                                                    <label htmlFor="month">Month</label>
                                                    <input type="text" name="month" id="month" className="form-control input-lg text-center edit" value={crime.crime.month} disabled />
                                                </div>
                                            </div>
                                            <div className="row md-3">                                                
                                                <div className="col">
                                                    <button className="btn btn-secondary" id="cancelEdition" disabled={true} onClick={() => this.handleCancel()}> CANCEL</button>
                                                </div>
                                                <div className="col">
                                                    <button className="btn btn-md btn-primary" id="saveEdition" disabled={true} onClick={() => this.handleSave()} >SAVE</button>
                                                </div> 
                                                <div className="col">
                                                    <button className="btn btn-md btn-primary" id="editOutcomes" onClick={() => this.handleEdit()} >EDIT</button>
                                                </div>
                                                
                                            </div>
                                    </form>
                                    <div className="row col-md-8">
                                        <div className='col self-align-center'>
                                            <h4 className="text-uppercase col-md-10" > OUTCOMES</h4>
                                        </div>
                                        
                                        <table className="table table-striped col-md-12">
                                            <thead>
                                                <tr>
                                                    <th scope="col">ID</th>
                                                    <th scope="col">Category</th>
                                                    <th scope="col">Month</th>
                                                    <th scope="col">Person ID</th>
                                                    <th scope="col"></th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                            {   crime.outcomes.map(outcome => (
                                                    <tr key={outcome.id}>
                                                        <th scope="row">{outcome.id}</th>
                                                        <td>{outcome.category}</td>
                                                        <td>{outcome.date_1}</td>
                                                        <td>{outcome.person_id}</td>
                                                        <td><button id="cancelOutcomes" className="btn btn-md btn-secondary cancel" disabled>DELETE</button></td>  
                                                    </tr>
                                                    )
                                                )}

                                            </tbody>
                                        </table>
                                    </div>
                                </fieldset>
                            </center>
                        </div>                        
                    </div>
                </div>
            </div>
            </React.Fragment>
         );
    }
