import React, { Component } from 'react';
import Like from './like';

class Movies extends Component {
    state = { movies: [
                { id:1, movie: {
                    title: "Terminator",
                    genre: {
                        name: "Action"
                    },
                    numberInStock: 6,
                    dailyRentalRate: 2.5,
                    liked: true
                    }
                },
                { id:2, movie: {
                    title: "Die Hard",
                    genre: {
                        name: "Action"
                    },
                    numberInStock: 5,
                    dailyRentalRate: 2.5,
                    liked: true
                    }
                },
                { id:3, movie: {
                    title: "Get Out",
                    genre: {
                        name: "Thriller"
                    },
                    numberInStock: 8,
                    dailyRentalRate: 3.5,
                    liked: true
                    }
                }
            ]
    }
    render() { 
        console.log(this.state.movies)
        return ( 
            <React.Fragment>
                <p>Showing {this.state.movies.length} movies in the database.</p>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Genre</th>
                            <th>Stock</th>
                            <th>Rate</th>
                            <th>Like</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.movies.map(movie => (
                            <tr key={movie._id}>
                                <td>{movie.title}</td>
                                <td>{movie.genre.name}</td>
                                <td>{movie.numberInStock}</td>
                                <td>{movie.dailyRentalRate}</td>
                                <td><Like /></td>
                                <td>
                                    <button
                                        onClick={() => this.handleDelete(movie)}
                                        className="btn btn-danger btn-sm"
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </React.Fragment>
         );
    }
}
 
export default Movies;
