import React, { Component, useState } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';
import { AppContext } from "./libs/contextLib";
import NavBar from './navbar';
import CrimeComponent from './crime/crimeComponent';
import Login from './account/login';
import Signup from './account/signup';
import axios from 'axios';
import _ from 'lodash';

class App extends Component {
    state = {
        post:[],
        elements: [],
        categories: [],
        selectedCategory: 'allCategories',
        itemsCount:0,
        pageSize:5,
        currentPage: 1,
        sortColumn: {path: 'id', order:'asc'},
        session: false
    }

    async componentDidMount() {
        const {data:post} = await axios.get('http://localhost/home');
        this.setState({post});

        const cat = [];
        
        this.state.post.map(c => {
              cat.push(c.category_id)
        });
        const categories = _.uniq(cat);
        this.setState({ categories });

        const elements = this.state.selectedCategory === 'allCategories' ?  this.state.post: null;
        this.setState({ elements })

        const itemsCount = this.state.elements.length;
        this.setState({itemsCount})
    }
    // componentDidUpdate(){
    //     console.log(this.state);
    // }

    handleReset = () => {
         const counters = this.state.counters.map(
             c => {
                 c.value = 0;
                 return c
             }
         )
         this.setState({ counters })
         
     }
    handleIncrement = counterId =>{
        const counters = this.state.counters.map(
                c => {
                    if ( c.id === counterId){
                        c.value = c.value + 1;
                    }                    
                    return c
                }
            );

        this.setState({ counters })

    }
    handleDecrement = counterId => {
        const counters = this.state.counters.map(
            c => {
                if(c.id === counterId){
                    c.value = c.value - 1;
                }
                return c
            }
        );
        this.setState(counters);
    }
    handleDelete = counterId => {
        const counters = this.state.counters.filter(c => c.id !== counterId)
        this.setState({ counters })
    }
    handleDeleteMovie = movieId => {
        const movies = this.state.movies.filter(c => c.movie.id !== movieId);
        this.setState({ movies });
    }
    handleLike = movieId => {
        let id = movieId;
        const movies = this.state.movies.filter(c =>{
            if(c.movie.id === id){
                c.movie.liked = !c.movie.liked;
            }
            return c
        })
        this.setState({ movies })
    }
    handlePageChange = page => {
        this.setState({ currentPage:page })
    }
    handleSort = path => {
        const sortColumn = { ...this.state.sortColumn }
        if (sortColumn.path === path){
            sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';
        } else{
            sortColumn.path = path;
            sortColumn.order = 'asc';
        }
        this.setState({sortColumn});
    }
    
    async handlesubmitSignup(e){
        e.preventDefault();
        const usr = {
            firstName: document.getElementById('firstName').value,
            lastName: document.getElementById('lastName').value,
            username: document.getElementById('username').value,
            email: document.getElementById('email').value,
            password: document.getElementById('password').value

        };

        const {data:aut} = await axios.post('http://localhost/signup',usr);
    }
    selectedCategoryChange = category  => {
        const selectedCategory = category;
        this.setState({ selectedCategory });

        const currentPage = 1;
        this.setState({ currentPage });

    }

    render() { 
        const [isAuthenticated, userHasAuthenticated] = useState(false);
        return ( 
            <React.Fragment>
                <AppContext.Provider value={{ isAuthenticated, userHasAuthenticated }}>
                    <NavBar session= { this.state.session } />
                    { this.state.session ? <Redirect to='/' /> : <Redirect to='/login' /> }
                    <Switch>
                        <Route path="/login">
                            <Login />
                        </Route>
                        <Route path="/signup">
                            <Signup onSubmit={this.handlesubmitSignup} />
                        </Route>
                        <Route path="/">
                            <CrimeComponent 
                                out={this.state.elements} 
                                onLike={this.handleLike} 
                                onDelete={this.handleDeleteMovie} 
                                onPageChange={this.handlePageChange} 
                                itemsCount={this.state.itemsCount}
                                pageSize={this.state.pageSize}
                                currentPage={this.state.currentPage}
                                allCategories={this.state.categories}
                                selectedCategory={this.state.selectedCategory}
                                onCategoryChange={this.selectedCategoryChange}
                                sortColumn={this.state.sortColumn}
                                onSort={this.handleSort}
                            />
                        </Route>
                    </Switch>
                </AppContext.Provider>
            </React.Fragment>
         );
    }
}
 
export default App;
