import React, { Component } from 'react';


class Counter extends Component {
    state = {
        count: 2
    }
    render() {
        return ( 
        <React.Fragment>
            <span className={this.getBadgeClass()}>{this.formatcount()}</span> <br></br>
            <button className="btn btn-secondary btn-sm m-2">Click</button>
        </React.Fragment> 
        );
    }

    getBadgeClass() {
        let classes = "badge m-2 badge-";
        classes += this.state.count === 0 ? "warning" : "primary";
        return classes;
    }

    formatcount(){
        const { count } = this.state;
        return count === 0 ? 'Zero' : count;
    }
}
 
export default Counter;
