import React, { Component, useState } from 'react';
import { Route, Switch, Redirect, useLocation } from 'react-router-dom';
import NavBar from './navbar';
import Crime from './crime/crimes';
import Login from './account/login';
import Signup from './account/signup';
import loadCrimes from './crime/loadCrimes';
import userService from './account/userService';
import crimeServices from './crime/crimeServices';
import axios from 'axios';
import _ from 'lodash';
import LoadCrimes from './crime/loadCrimes';
import CrimeDetails from './crime/crimeDetails';

class App extends Component {
    state = {
        crimes:[],
        elements: [],
        categories: [],
        selectedCategory: 'allCategories',
        itemsCount:0,
        pageSize:5,
        currentPage: 1,
        sortColumn: {path: 'id', order:'asc'},
        session: false,
        token: false,
        crime: []
    }
        constructor(){
        super()
        this.handleSubmitLogin = this.handleSubmitLogin.bind(this);
    }
    async componentDidMount() {
        const crimes = await crimeServices.getCrimes();
        this.setState({crimes});

        // const cat = [];
        
        // this.state.post.map(c => {
        //       cat.push(c.category_id)
        // });
        // const categories = _.uniq(cat);
        // this.setState({ categories });

        // const elements = this.state.selectedCategory === 'allCategories' ?  this.state.post: null;
        // this.setState({ elements })

        // const itemsCount = this.state.elements.length;
        // this.setState({itemsCount})

        const token = userService.getSession();
        if(token){
            const session = true;
            this.setState({ session })
            this.setState({ token });
        }
    }
    // componentDidUpdate(){
    //     console.log(this.state);
    // }

    handleReset = () => {
         const counters = this.state.counters.map(
             c => {
                 c.value = 0;
                 return c
             }
         )
         this.setState({ counters })
         
     }
    handleIncrement = counterId =>{
        const counters = this.state.counters.map(
                c => {
                    if ( c.id === counterId){
                        c.value = c.value + 1;
                    }                    
                    return c
                }
            );

        this.setState({ counters })

    }
    handleDecrement = counterId => {
        const counters = this.state.counters.map(
            c => {
                if(c.id === counterId){
                    c.value = c.value - 1;
                }
                return c
            }
        );
        this.setState(counters);
    }
    handleDelete = counterId => {
        const counters = this.state.counters.filter(c => c.id !== counterId)
        this.setState({ counters })
    }
    handleDeleteMovie = movieId => {
        const movies = this.state.movies.filter(c => c.movie.id !== movieId);
        this.setState({ movies });
    }
    // handleLike = movieId => {
    //     let id = movieId;
    //     const movies = this.state.movies.filter(c =>{
    //         if(c.movie.id === id){
    //             c.movie.liked = !c.movie.liked;
    //         }
    //         return c
    //     })
    //     this.setState({ movies })
    // }
    // handlePageChange = page => {
    //     this.setState({ currentPage:page })
    // }
    // handleSort = path => {
    //     const sortColumn = { ...this.state.sortColumn }
    //     if (sortColumn.path === path){
    //         sortColumn.order = (sortColumn.order === 'asc') ? 'desc' : 'asc';
    //     } else{
    //         sortColumn.path = path;
    //         sortColumn.order = 'asc';
    //     }
    //     this.setState({sortColumn});
    // }
    async handleSubmitLogin(e) {
        e.preventDefault();
        const usr = {
            userName: document.getElementById('userName').value,
            password: document.getElementById('password').value
          }
        if(userService.login(usr)){
            const session = true;
            this.setState({session}); 
        }    
    }
    handleLogOut = () => {
        
        if (!userService.signout()) {
            const session = false;
            this.setState({ session });
        }
        
    }
    handlesubmitLoad = e => {
        e.preventDefault();
        const date = { 'date': document.getElementById('date').value};
        crimeServices.loadCrimes(date);
    }
    handlesubmitSignup = async e => {
        e.preventDefault();
        const usr = {
            firstName: document.getElementById('firstName').value,
            lastName: document.getElementById('lastName').value,
            username: document.getElementById('username').value,
            email: document.getElementById('email').value,
            password: document.getElementById('password').value

        };

        const signUp = userService.signUp(usr);
    }
    handleCrimesDetails = id =>{
        // const crime = crimeServices.crimeDetails(id);
        // this.setState({crime});
        // return <Redirect to='/edit' />
        return ''

    }
    selectedCategoryChange = category  => {
        const selectedCategory = category;
        this.setState({ selectedCategory });

        const currentPage = 1;
        this.setState({ currentPage });

    }
    render() { 
        return ( 
            <React.Fragment>
                <NavBar session= { this.state.session } logOut={this.handleLogOut} />
                <Switch>
                    <Route path="/login">
                        { this.state.session ? <Redirect to='/' /> : <Redirect to='/login' /> }
                        <Login onSubmit={this.handleSubmitLogin} />
                    </Route>
                    <Route path="/signup">
                        { this.state.session ? <Redirect to='/' /> : <Redirect to='/signup' /> }
                        <Signup onSubmit={this.handlesubmitSignup} />
                    </Route>
                    <Route path="/loadCrimes">
                        <LoadCrimes onSubmit={ this.handlesubmitLoad }/>
                    </Route>
                    <Route path='/crimeDetails/:id' component={CrimeDetails} />
                    <Route path="/">
                        { this.state.session ? <Redirect to='/' /> : <Redirect to='/login' /> }
                        <Crime
                            crimes={this.state.crimes}
                        />
                    </Route>
                </Switch>
            </React.Fragment>
         );
    }
}
 
export default App;
