import React, { Component } from 'react';
import NavBar from './navbar';
import Counter from './counters';

class App extends Component {
    state = { 
        counters: [
            {id:1, value:0},
            {id:2, value:0},
            {id:3, value:0},
            {id:4, value:0}
        ]
     }
    // state = { movies: [
    //         {  movie: {
    //             id:1,
    //             title: "Terminator",
    //             genre: {
    //                 name: "Action"
    //             },
    //             numberInStock: 6,
    //             dailyRentalRate: 2.5,
    //             liked: false
    //             }
    //         },
    //         { movie: {
    //             id:2,
    //             title: "Die Hard",
    //             genre: {
    //                 name: "Action"
    //             },
    //             numberInStock: 5,
    //             dailyRentalRate: 2.5,
    //             liked: true
    //             }
    //         },
    //         { movie: {
    //             id:3,
    //             title: "Get Out",
    //             genre: {
    //                 name: "Thriller"
    //             },
    //             numberInStock: 8,
    //             dailyRentalRate: 3.5,
    //             liked: true
    //             }
    //         }
    //     ]
    // }
     handleReset = () => {
         const counters = this.state.counters.map(
             c => {
                 c.value = 0;
                 return c
             }
         )
         this.setState({ counters })
         
     }
    handleIncrement = counterId =>{
        const counters = this.state.counters.map(
                c => {
                    if ( c.id === counterId){
                        c.value = c.value + 1;
                    }                    
                    return c
                }
            );

        this.setState({ counters })

    }
    handleDecrement = counterId => {
        const counters = this.state.counters.map(
            c => {
                if(c.id === counterId){
                    c.value = c.value - 1;
                }
                return c
            }
        );
        this.setState(counters);
    }
    handleDelete = counterId => {
        const counters = this.state.counters.filter(c => c.id !== counterId)
        this.setState({ counters })
    }
    handleLike = movieId => {
        let id = movieId.movie.id;
        const movies = this.state.movies.filter(c =>{
            if(c.id === id){
                c.liked = !c.liked;
            }
            return c
        })
        this.setState({ movies })
    }
    render() { 
        return ( 
            <React.Fragment>
                <NavBar />
                <button onClick={this.handleReset}
                className="btn btn-secondary btn-sm m-2">Reset</button><br></br>
                <div className="row">
                    {this.state.counters.map(counter => (
                        <Counter 
                            key={counter.id} 
                            onDelete={this.handleDelete}
                            value={counter.value} 
                            id={counter.id}
                            onIncrement={this.handleIncrement}
                            onDecrement={this.handleDecrement}
                        />
                    ))}
                </div>               
                {/* <Counter movies={this.state.movies} onLike={this.handleLike}/> */}
            </React.Fragment>
         );
    }
}
 
export default App;
